                    break;
                case 0x002E:
                    position.set(Position.KEY_ODOMETER, buf.readUnsignedInt());
                    break;
                case 0x003B:
                    position.setAccuracy(buf.readUnsignedShort() * 0.01);
                    break;
                default:
