                break;
            case 0x92:
                position.set(Position.KEY_ALARM, Position.ALARM_CORNERING);
                break;
            case 0x93:
                position.set(Position.KEY_ALARM, Position.ALARM_ACCIDENT);
                break;
            default:
                position.set(Position.KEY_ALARM, Position.ALARM_GENERAL);
                break;
        }

