        position.set(Position.KEY_BATTERY, buf.readUnsignedShort() * 0.1);
        buf.readUnsignedInt(); // area id
        position.set(Position.KEY_RSSI, buf.readUnsignedByte());
        buf.skipBytes(3); // reserved

        return position;

